mapper.update:
UPDATE TICKETING
SET TICKETING_FLAG = 'Y'
WHERE TICKETING_ID = #{ticketing_id} AND CUSTOMER_ID = #{customer_id}

UPDATE REVIEW
SET REVIEW_TITLE= #{review_title} , REVIEW_CONTENTS = #{review_contents} , REVIEW_STAR_SCORE =#{review_star_score}
WHERE REVIEW_ID  =#{review_id}

mapper.select:
SELECT * FROM ( SELECT e.EXHIBITION_ID , e.EXHIBITION_TITLE ,e.EXHIBITION_START_DATE , e.EXHIBITION_END_DATE , e.EXHIBITION_IMAGE , e.EXHIBITION_LOCATION , r.REVIEW_TITLE , r.REVIEW_CONTENTS , r.REVIEW_DATE , r.REVIEW_STAR_SCORE , r.REVIEW_ID , r.CUSTOMER_ID FROM HAN.EXHIBITION_INFO e , HAN.REVIEW r WHERE e.EXHIBITION_ID = r.EXHIBITION_ID ORDER BY r.REVIEW_ID DESC)
ROWNUM <= ${endRow}
AND EXHIBITION_TITLE LIKE '%${searchKeyword}%'
